import React from "react";
import { blogposts } from "../../data";
import "./store_blog.css";

const index = () => {
  return (
    <div className="store__blog">
      <div className="store__blog-container store__container">
        {blogposts.map((post) => (
          <article className="store__blog--body" key={post.id}>
            <figure className="store__body--image">
              <img src={post.img} alt="User" />
            </figure>
            <h2 className="store__body--title">{post.title}</h2>
            <p className="store__body--content">{post.content}</p>
            <small className="store__body--date">{post.authorAndDate}</small>
          </article>
        ))}
      </div>
    </div>
  );
};

export default index;
