import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./store_header.css";
import shoppingBag from "../../assets/icons/shopping-bag.svg";
import user from "../../assets/icons/user.svg";
import hamburger from "../../assets/icons/burger.svg";

const StoreHeader = () => {
  const [small, setSmall] = useState(false);

  useEffect(() => {
    if (typeof window !== "undefined") {
      window.addEventListener("scroll", () =>
        setSmall(window.pageYOffset > 60)
      );
    }
  }, []);

  return (
    <header className="store__header">
      <nav className={`store__navbar ${small ? "store__sticky-header" : ""}`}>
        <div className="store__container">
          <Link to={"/"} className="store__navbar--logo" title="Logo">
            Venezza
          </Link>

          <nav className="store__navbar--nav">
            <a href="/#new-collection">Novidades</a>
            <a href="/#sale">Promoções</a>
          </nav>

          <div className="">
            <img className="" src={shoppingBag} alt="Sacola" title="Sacola" />
            <img className="" src={user} alt="usuário" title="Usuário" />
            <img className="store__hamburger" src={hamburger} alt="hamburger" />
          </div>
        </div>
      </nav>
    </header>
  );
};

export default StoreHeader;
