import React from 'react'
import { Spinner } from 'react-bootstrap';


const StoreLoader = () => {
  return (
    <Spinner
     animation={'border'}
     role={'status'}
     style={{ width: '100px', height: '100px', margin: 'auto', display: 'block'}}
    >
     <span>Carregando...</span> 
    </Spinner>
  )
}

export default StoreLoader
