import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import StoreProductCard from "../common/StoreProductCard";
import shoppingbag from "../../assets/icons/shopping-bag.svg";
import quicklook from "../../assets/icons/eye.svg";
import StoreSections from "../common/StoreSections";
import { Pagination, Navigation } from "swiper";
import "./store_productSlider.css";
import "swiper/css/bundle";

const StoreProductSlider = ({ products }) => {
  return (
    <div className="store__grid-container">
      <StoreSections title="Nova Coleção" subtitle="Novidades" />

      <div className="store__slider store__container">
        <Swiper
          slidesPerView={4}
          spaceBetween={20}
          slidesPerGroup={4}
          loop={true}
          cssMode={true}
          //  autoplay={{
          //    delay: 6500,
          //    disableOnInteraction: false,
          //  }}
          loopFillGroupWithBlank={true}
          pagination={{
            clickable: true,
          }}
          navigation={true}
          modules={[Pagination, Navigation]}
          breakpoints={{
            // 420: {
            //   slidesPerView: 1,
            //   spaceBetween: 20,
            // },
            520: {
              slidesPerView: 1,
              spaceBetween: 20,
            },
            640: {
              slidesPerView: 2,
              spaceBetween: 20,
            },
            992: {
              slidesPerView: 3,
              spaceBetween: 30,
            },
            1240: {
              slidesPerView: 4,
              spaceBetween: 20,
            },
          }}
          className="store__slider--swiper"
        >
          {products.map((product, i) => (
            <SwiperSlide key={product._id}>
              <StoreProductCard
                productImage={product.img}
                productPrice={product.price}
                productName={product.name}
                id={product._id}
                bagIcon={shoppingbag}
                eyeIcon={quicklook}
              />
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </div>
  );
};

export default StoreProductSlider;
