import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import StoreProductImage from "../../components/StoreProductImage";
import StoreProductDescription from "../../components/StoreProductDescription";
import "./store_detailspage.css";

import AddButton from "../common/StoreButtons/StoreButtonOne";
import StoreSocialMedia from "../common/StoreSocialMedia";
import exit from "../../assets/icons/exit-icon.svg";

const StoreDetails = () => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <main className="store__product-details store__container">
      <Link to={"/"} className="store__product-details--goback">
        <img src={exit} alt="Sair" />
        Voltar
      </Link>
      <article className="store__product-details--wrapper">
        <StoreProductImage />
        <article className="store__product-details--description">
          <h1>BLUSA DALTEX</h1>
          <h2>R$ 39.99</h2>

          <span></span>
          <p>
            Lorem ipsum, dolor sit amet consectetur adipisicing elit. Ratione,
            illum eum officiis ex consectetur vero nobis ullam, natus, iusto
            necessitatibus iure voluptatibus. Quia est adipisci officia porro
            perspiciatis voluptas laborum ad eius totam nam alias at, iure
            reprehenderit labore architecto cumque. Laborum praesentium odio
            possimus consequuntur laudantium minima ex magni exercitationem
            quisquam accusamus asperiores
          </p>
          <form className="store__description--action-select">
            <select name="" id="">
              <option value="Selecione">Selecione</option>
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
              <option value="5">5</option>
            </select>
            <AddButton title={"adicionar ao carrinho"} type="submit" />
          </form>
          <span></span>
          <StoreSocialMedia />
        </article>
      </article>
    </main>
  );
};

export default StoreDetails;
