import React, { useEffect } from 'react';
import { Link, useParams, useLocation } from "react-router-dom";
import { useDispatch, useSelector } from 'react-redux';
import Message from '../../components/StoreMessages/StoreMessage';
import exit from "../../assets/icons/exit-icon.svg";
import trash from "../../assets/icons/trash.svg";
import { addToCart } from '../../actions/cartActions';
import './store_cartpage.css';

import placeholder from '../../assets/images/products/product-12.jpg'

const StoreCartpage = ( ) => {
  const params = useParams();  
  let location = useLocation();
  const dispatch = useDispatch();

  const productId = params.id;
  const qty = location.search ? +location.search.split('=')[1] : 1;

  const cart = useSelector(state => state.cart);
  const { cartItems } = cart;
 

  useEffect(() => {
     if(productId) {
      dispatch(addToCart(productId, qty))
     }
  }, [dispatch, productId, qty])


  return (
    <main className="store__container">
      <div className="store__product-cart">
           <Link className="store__product-details--goback" to={"/"}>
              <img src={exit} alt="Sair" />
              Voltar
          </Link>
          <br />
          <br /> 
           <div>
              <div style={{display: 'flex',justifyContent:'center', marginBottom: '6  0px' }}>
                <h1>Carrinho</h1>
              </div>

              <ul>
                  <li style={{display: 'flex',justifyContent:'space-between', alignItems: 'flex-start' }}>
                    <div style={{display: 'flex', gap: '20px'}}>
                        <img src={placeholder} alt="Placeholder" style={{maxWidth: '130px'}}/>
                        <div>
                           <h2>Blusa feminina</h2>
                            <p>Cinza escuro</p>
                            <p>M</p>
                          <p> R$ 99,90</p>
                        </div>
                    </div> 
                    <img src={trash} alt="Deletar" title='Deletar esse item'/> 
                  </li>
              </ul>
           </div>
           {/* <ul>
              {cartItems.length && cartItems.map((item, i) => ( 
                  <li key={i}>{item.name}</li> 
              ))}
           </ul> */}
      </div>
    </main>
  )
}

export default StoreCartpage;
